<?phpfunction sendEmail() {  $request = Slim::getInstance()->request();  $emailPostData = json_decode($request->getBody());  if (isset($emailPostData->email) && !empty($emailPostData->email) &&          isset($emailPostData->mensaje) && !empty($emailPostData->mensaje) &&          isset($emailPostData->name) && !empty($emailPostData->name)) {		  echo MessageHandler::getSuccessResponse("El email ha sido enviado, gracias por contactarnos!", null);    }